import React from "react";

function WeatherIcon({ condition }) {
  const iconMap = {
    Sunny: "‚òÄÔ∏è",
    Cloudy: "‚òÅÔ∏è",
    Rainy: "üåßÔ∏è",
    Snow: "‚ùÑÔ∏è"
  };

  return (
    <div>
      <h3>Condition: {condition}</h3>
      <p>{iconMap[condition] || "‚ùî"}</p>
      <style jsx>{`
        div {
          text-align: center;
          font-size: 2rem;
        }
      `}</style>
      <style jsx>{`
        h3 {
          color: #333;
        }
      `}</style>
      <style jsx>{`
        p {
          font-size: 3rem;
        }
      `}</style>
      <style jsx>{`
        div:hover {
          transform: scale(1.1);
          transition: transform 0.2s;
        }
      `}</style>
      <style jsx>{`
        div:active {
          transform: scale(0.9);
          transition: transform 0.1s;
        }
      `}</style>
      <style jsx>{`
        div:focus {
          outline: none;
        }
      `}</style>
    </div>
  );
}

export default WeatherIcon;
